mapper.insert:
INSERT INTO orders (o_no, o_name, o_total, o_fee, o_tel, o_addr, o_detail, m_no, b_no)
SELECT 
    orders_seq.nextval, 
    #{dto.o_name}, 
    NVL((SELECT SUM(b_sumprice) FROM basket WHERE b_no IN 

            #{id}

    ), 0) + 
    (CASE WHEN NVL((SELECT SUM(b_sumprice) FROM basket WHERE b_no IN 

            #{id}

    ), 0) >= 15000 THEN 0 ELSE 3000 END),

    (CASE WHEN NVL((SELECT SUM(b_sumprice) FROM basket WHERE b_no IN 

            #{id}

    ), 0) >= 15000 THEN 0 ELSE 3000 END),

    #{dto.o_tel}, #{dto.o_addr}, #{dto.o_detail}, 

    (SELECT m_no FROM basket WHERE b_no = TO_NUMBER(#{b_no_list[0]})),

    TO_NUMBER(#{b_no_list[0]})
FROM DUAL

mapper.delete:
DELETE FROM basket 
WHERE b_no IN 

    #{id}
